  method: 'POST',
  url: endpoint,
  body: {
    orderId: "' OR 1=1 --",
    amount: 100
  },
  failOnStatusCode: false
}).then(res => {
  expect(res.status).to.eq([400, 404]);
  expect(res.body.status).to.eq('FAILED');
  expect(res.body).to.have.property('error', 'Invalid orderId');
});

